import React, { useState, useEffect } from "react";
import axios from "axios";
import NasaCard from "./components/NasaCard";
import DatePickerComp from "./components/DatePicker";
import styled from "styled-components";
import "./scss/App.css";

const Title = styled.h1`
  @import url("https://fontlibrary.org/face/space-meatball");
  text-align: center;
  font-family: "SpaceMeatballRegular";
  font-size: 2.5em;
  font-weight: 400;
  color: rgb(221, 227, 255);
  width: 100%;
`;

function App() {
  const [nasaData, setNasaData] = useState([]);
  const [selectedDate, setSelectedDate] = useState(
    new Date("2019/01/17").toISOString().split("T")[0]
  );

  useEffect(() => {
    axios
      .get(
        `https://api.nasa.gov/planetary/apod?api_key=6VFteJXqOzwuqeItB8JzRG1IJK6KWCjuew4iH6nr&date=${selectedDate}`
      )
      .then(res => {
        setNasaData(res.data);
        console.log("RESPONSE:", res.data);
      })
      .catch(err => console.log("YOU MESSED UP SOMETHING:", err));
  }, [selectedDate]);

  return (
    <div className="App">
      <Title>Nasa Photo of the Day</Title>

      <div className="calendar">
        <DatePickerComp
          selectedDate={selectedDate}
          setSelectedDate={setSelectedDate}
        />
      </div>
      <div>
        <NasaCard
          title={nasaData.title}
          media_type={nasaData.media_type}
          date={nasaData.date}
          url={nasaData.url}
          explanation={nasaData.explanation}
        />
      </div>
    </div>
  );
}

export default App;
