import React from "react";
import { Card, Image, Button, Header, Embed, Modal } from "semantic-ui-react";
import "semantic-ui-css/semantic.min.css";
import styled from "styled-components";
import ".././scss/App.css";

function NasaCard(props) {
  const ModButton = styled.button`
    background-color: rgba(0, 0, 0, 0.1);
  `;

  if (props.media_type === "video") {
    return (
      <div className="cardCont">
        <Card className="centered fluid card">
          <Embed className="cardVid" url={props.url} />
          <Card.Header className="title">{props.title}</Card.Header>
          <Card.Meta>
            <span className="date">{props.date}</span>
          </Card.Meta>
          <Card.Description>{props.explanation}</Card.Description>
        </Card>
      </div>
    );
  } else {
    return (
      <Card className="centered fluid">
        <Image
          className="cardImg"
          src={props.url}
          wrapped
          ui={false}
          alt="Nasa API photo"
        />
        <Card.Header className="title">{props.title}</Card.Header>
        <Card.Meta>
          <span className="date">{props.date}</span>
        </Card.Meta>
        <Modal trigger={<Button>More Info</Button>}>
          <Modal.Header>{props.title}</Modal.Header>
          <Modal.Content>
            <Modal.Description>
              <Header>{props.date}</Header>
              <p>{props.explanation}</p>
            </Modal.Description>
          </Modal.Content>
        </Modal>
      </Card>
    );
  }
}

export default NasaCard;
