import React from "react";
import { Card, Image, Embed, Item } from "semantic-ui-react";
import "semantic-ui-css/semantic.min.css";
import styled from "styled-components";
import ".././scss/App.css";

const CardContainer = styled.div`
  max-width: 500px;
  margin: 1em auto;
  text-align: center;

  .card {
    padding: 10px;
    background-color: rgba(0, 0, 0, 0.7);
    color: white;

    .cardImg {
      width: 400px;
      margin: 1em auto;
    }

    .cardVid {
      margin: 1em auto;
    }

    .title {
      font-size: 1.5em;
    }

    .date {
      color: rgb(211, 208, 231);
    }
  }
`;

const items = [
  {
    childKey: 0,
    image: props.url,
    header: "Header",
    description: "Description",
    meta: "Metadata",
    extra: "Extra"
  }
];

function NasaCard(props) {
  if (props.media_type === "video") {
    return (
      <div className="cardCont">
        <Card className="centered fluid card">
          <Embed className="cardVid" url={props.url} />
          <Card.Header className="title">{props.title}</Card.Header>
          <Card.Meta>
            <span className="date">{props.date}</span>
          </Card.Meta>
          <Card.Description>{props.explanation}</Card.Description>
        </Card>
      </div>
    );
  } else {
    return (
      <CardContainer>
        <Card className="centered fluid">
          <Image
            className="cardImg"
            src={props.url}
            wrapped
            ui={false}
            alt="Nasa API photo"
          />
          <Card.Header className="title">{props.title}</Card.Header>
          <Card.Meta>
            <span className="date">{props.date}</span>
          </Card.Meta>
          <Card.Description className="explanation">
            {props.explanation}
          </Card.Description>
        </Card>
        <div>
          <Item.Group items={items} />
        </div>
      </CardContainer>
    );
  }
}

export default NasaCard;
