import React from "react";
import { Card, Image, Button, Header, Embed, Modal } from "semantic-ui-react";
import "semantic-ui-css/semantic.min.css";
import ".././scss/App.css";

function NasaCard(props) {
  if (props.media_type === "video") {
    return (
      <div className="cardCont">
        <Card className="centered fluid card">
          <Embed className="cardVid" url={props.url} />
          <Card.Header className="title">{props.title}</Card.Header>
          <Card.Meta>
            <span className="date">{props.date}</span>
          </Card.Meta>
          <Card.Description>{props.explanation}</Card.Description>
        </Card>
      </div>
    );
  } else {
    return (
      <div className="cardCont">
        <Card className="centered fluid">
          <Image
            className="cardImg"
            src={props.url}
            wrapped
            ui={false}
            alt="Nasa API photo"
          />
          <Modal trigger={<Button>Show Modal</Button>}>
            <Modal.Header>Select a Photo</Modal.Header>
            <Modal.Content>
              <Modal.Description>
                <Header>Default Profile Image</Header>
                <p>
                  We've found the following gravatar image associated with your
                  e-mail address.
                </p>
                <p>Is it okay to use this photo?</p>
              </Modal.Description>
            </Modal.Content>
          </Modal>
          <Card.Header className="title">{props.title}</Card.Header>
          <Card.Meta>
            <span className="date">{props.date}</span>
          </Card.Meta>
          <Card.Description className="explanation">
            {props.explanation}
          </Card.Description>
        </Card>
      </div>
    );
  }
}

export default NasaCard;
